import { Avatar, makeStyles } from '@material-ui/core';
import { Grid } from '@mui/material';
import React from 'react';
import avatar  from './avatar.png'

const useStyles = makeStyles((theme) => ({
  aboutContainer:{
    backgroundColor: "darkolivegreen",
    minHeight: "100vh",
    display: 'flex',
    flexDirection: "column",
    justifyContent: 'space-around',
  },

  span1:{
    backgroundColor: '#009688',
    textAlign:'center',
    padding: '0  0.5rem',
  },

  span2:{
    textAlign: "center",
    padding: '0rem  0.2rem',
    boxShadow: 'inset 0 0 1500px rgba(250, 250, 255, .3)',
  },

  span3:{
    textDecoration: 'underline',
    textDecorationColor: '#4DD0E1',
  },

  titleContainer:{
    fontSize: '1.5rem',
    color:'white',
    margin: '5rem  1rem 0rem 1.1rem',
    },

    aboutDiv:{
      backgroundColor: '#009688',
      fontSize: '3rem',
    },
  }))



const About = () => {
  const classes = useStyles();
  return (
      <div className={classes.aboutContainer}>
        <div className={classes.titleContainer}>
            <h2 className="mx-0 text-lg text-center">I want to <span className={classes.span1}>make things</span> <span className={classes.span2}>that <span className={classes.span3}>make a difference</span></span> </h2>
         </div>
        <div className="mx-auto ">
          <img src={avatar} className=" w-[15rem] h-[15rem]  rounded-lg" alt="Shindano Singi" />
        </div>
        <div className="mt-1 w-[15rem] px-2 text-center">
          I am a Full Stack Developer with strong skills in both the backend and the front-end of web applications. Capable of quickly learning and mastering new technologies and working in both team and self-directed settings. I love solving algorithmically complex problems, and have passion for coding and building applications.

          My current career as a Systems Engineer at Siemens brought me close to my true passion: Full Stack Developer. At Siemens, I design systems for projects that are worth about 1 million dollars using AutoCAD, Design and Commissioning tools, Microsoft Excel, Adobe Acrobat, and Bluebeamâ€¦
          Also, I work in a group of 2 to 5 engineers on projects that are worth millions of dollars.

          In the next phase of my career, I want to use both my design skills and also work on the technically challenging facets of the application.

          I am seeking a role as a Full Stack Developer, software engineer or software developer at a company where I can learn, grow, and continue to master the craft.
        </div>
        </div>
        );
      }

export default About;
