import { Avatar, makeStyles } from '@material-ui/core';
import { Grid } from '@mui/material';
import React from 'react';
import avatar  from './avatar.png'

const useStyles = makeStyles((theme) => ({
  aboutContainer:{
    backgroundColor: "darkolivegreen",
    minHeight: "100vh",
    display: 'flex',
    flexDirection: "column",
    justifyContent: 'space-around',
  },

  span1:{
    backgroundColor: '#009688',
    textAlign:'center',
    padding: '0  0.5rem',
  },

  span2:{
    textAlign: "center",
    padding: '0rem  0.2rem',
    boxShadow: 'inset 0 0 1500px rgba(250, 250, 255, .3)',
  },

  span3:{
    textDecoration: 'underline',
    textDecorationColor: '#4DD0E1',
  },

  titleContainer:{
    fontSize: '1.5rem',
    color:'white',
    margin: '5rem  1rem 0rem 1.1rem',
    },

    aboutDiv:{
      backgroundColor: '#009688',
      fontSize: '3rem',
    },
  }))



const About = () => {
  const classes = useStyles();
  return (
      <div className={classes.aboutContainer}>
        <div className={classes.titleContainer}>
            <h2 className="mx-0 text-lg text-center text-white">I want to <span className={classes.span1}>make things</span> <span className={classes.span2}>that <span className={classes.span3}>make a difference</span></span> </h2>
         </div>
        <div className="mx-auto ">
          <img src={avatar} className=" w-[92vw] h-[15rem] my-1  rounded-lg" alt="Shindano Singi" />
        </div>
        <div class="flex justify-center">
  <div class="block p-6 rounded-lg shadow-lg bg-white max-w-sm">
    <h5 class="text-gray-900 text-xl leading-tight font-medium mb-2">Card title</h5>
    <p class="text-gray-700 text-base mb-4">
    I am a <span className="bg-[#009688] px-1">Full Stack Developer</span>   with strong skills in both the <span className="bg-[#009688] px-1">backend</span> and the <span className="bg-[#009688] px-1">front-end</span>  of web applications. Capable of quickly learning and mastering new technologies and working in both team and self-directed settings. I love solving <span className="bg-[#009688] px-1">algorithmically</span>  complex problems, and have passion for coding and building applications.

My current career as a Systems Engineer at Siemens brought me close to my true passion: Full Stack Developer. At Siemens, I design systems for projects that are worth about 1 million dollars using AutoCAD, Design and Commissioning tools, Microsoft Excel, Adobe Acrobat, and Bluebeam…
Also, I work in a group of 2 to 5 engineers on projects that are worth millions of dollars.

In the next phase of my career, I want to use both my design skills and also work on the technically challenging facets of the application.

I am seeking a role as a <span className="bg-[#009688] px-1">Full Stack Developer</span>, <span className="bg-[#009688] px-1">software engineer</span> or <span className="bg-[#009688] px-1">software developer</span> at a company where I can learn, grow, and continue to master the craft.
    </p>
    <button type="button" class=" inline-block px-6 py-2.5 bg-blue-600 text-white font-medium text-xs leading-tight uppercase rounded shadow-md hover:bg-blue-700 hover:shadow-lg focus:bg-blue-700 focus:shadow-lg focus:outline-none focus:ring-0 active:bg-blue-800 active:shadow-lg transition duration-150 ease-in-out">Button</button>
  </div>
</div>

        {/* <div className="mt-1   text-xs  w-[95vw] px-1 text-white text-center">

        </div> */}
        </div>
        );
      }

export default About;
