const useStyles = makeStyles((theme) => ({
  aboutContainer:{
    backgroundColor: "darkolivegreen",
    minHeight: "100vh",
    display: 'flex',
    flexDirection: "column",
    justifyContent: 'space-around',
  },

  avatar: {
    height:'15rem',
    width:'15rem',
    margin: '0 auto',
    marginTop: '4rem',
    borderRadius: '1rem',
  },

  span1:{
    backgroundColor: '#009688',
    textAlign:'center',
    padding: '0  0.5rem',

