  span2:{
    textAlign: "center",
    padding: '0rem  0.2rem',
    boxShadow: 'inset 0 0 1500px rgba(255, 255, 255, .5)',
  },

  span3:{
    textDecoration: 'underline',
    textDecorationColor: '#4DD0E1',
  },

  titleContainer:{
    fontSize: '1.5rem',
    color:'white',
    margin: '5rem  1rem 0rem 1.1rem',
    },

    aboutDiv:{
      backgroundColor: '#009688',
      fontSize: '3rem',
    },
  }))

  const styles = (theme) => ({
    root: {
      padding: theme.spacing(1),
      [theme.breakpoints.down('md')]: {
        backgroundColor: theme.palette.secondary.main,
      },
      [theme.breakpoints.up('md')]: {
        backgroundColor: theme.palette.primary.main,
      },
      [theme.breakpoints.up('lg')]: {
        backgroundColor: green[500],
      },
    },
  });

const About = () => {
  const classes = useStyles();
  return (
    <div className={classes.aboutContainer}>
          <Avatar className={classes.avatar}  src={avatar} alt='Shindano Singi' />
          <div className={classes.titleContainer}>
              <h2>I want to <span className={classes.span1}>make things</span> <span className={classes.span2}>that <span className={classes.span3}>make a difference</span></span> </h2>
          </div>
    </div>
  );
}

export default About;
