import { Avatar, makeStyles } from '@material-ui/core';
import { Grid } from '@mui/material';
import React from 'react';
import avatar from './avatar.jpeg'

const useStyles = makeStyles((theme) => ({
  aboutContainer:{
    backgroundColor: "darkolivegreen",
    minHeight: "100vh",
    display: 'flex',
    flexDirection: "column",
    justifyContent: 'space-around',
  },

  avatar: {
    height:'15rem',
    width:'15rem',
    margin: '0 auto',
    marginTop: '4rem',
    borderRadius: '1rem',
  },

  span1:{
    backgroundColor: '#009688',
    textAlign:'center',
    padding: '0  0.5rem',

  },

  span2:{
    textAlign: "center",
    padding: '0rem  0.2rem',
    boxShadow: 'inset 0 0 1500px rgba(250, 250, 255, .3)',
  },

  span3:{
    textDecoration: 'underline',
    textDecorationColor: '#4DD0E1',
  },

  titleContainer:{
    fontSize: '1.5rem',
    color:'white',
    margin: '5rem  1rem 0rem 1.1rem',
    },

    aboutDiv:{
      backgroundColor: '#009688',
      fontSize: '3rem',
    },
  }))



const About = () => {
  const classes = useStyles();
  return (
      <div className={classes.aboutContainer}>
        <Grid>
          <Avatar className={classes.avatar}  src={avatar} alt='Shindano Singi' />
        </Grid>
          <div className={classes.titleContainer}>
              <h2 className="mx-0 mb-[2rem] text-xl md:bg-orange-700 text-center auto">I want to <span className={classes.span1}>make things</span> <span className={classes.span2}>that <span className={classes.span3}>make a difference</span></span> </h2>
          </div>
    </div>
  );
}

export default About;
